import React, { Component } from 'react'

class AddTodo extends Component {
    state = {
        content: '',
        quantity: '',
        department: ''
    }
    handleChangeContent = (e) => {
        this.setState({
            content: e.target.value
        })
    }
    handleChangeQuantity = (e) => {
      this.setState({
          quantity: e.target.value
      })
  }
  handleChangeDepartment = (e) => {
    this.setState({
        department: e.target.value
    })
}
    handleSubmit = (e) => {
       e.preventDefault();
       this.props.addTodo(this.state);
       this.setState({
           content:''
       })
    }
    render(){
        return(
            <div className="flex justify-center">
                <form onSubmit={this.handleSubmit} className="w-full p-4">
                  <div className="bg-white h-8 xxl:h-12 mt-3 flex mt-2">
                    <input type = "text" onChange={this.handleChangeContent} value={this.state.content} placeholder="Add new ingredient or recipe" className="py-1 xxl:py-3 px-4 w-1/2"/>
                    <input type = "text" onChange={this.handleChangeQuantity} value={this.state.quantity} placeholder="Quantity" className="py-1 xxl:py-3 px-4 w-1/4"/>
                    <select onChange={this.handleChangeDepartment} value={this.state.department} className="py-1 xxl:py-3 px-4 w-1/4">
                      <option value="fruit-veg">Fruit and Veg</option>
                      <option value="dairy">Dairy</option>
                      <option value="canned">Canned goods</option>
                    </select>
                    <button className="bg-teal h-8 xxl:h-12 w-1/12 relative text-white">
                      <p>Add</p>
                    </button>
                  </div>
                </form>
            </div>
        )
    }
}

export default AddTodo
