import React, { Component } from 'react'

function contentEditable(WrappedComponent) {

  return class extends React.Component {

    state = {
      editing: false
    }

    toggleEdit = (e) => {
      e.stopPropagation();
      if (this.state.editing) {
        this.cancel();
      } else {
        this.edit();
      }
    };

    edit = () => {
      this.setState({
        editing: true
      }, () => {
        this.domElm.focus();
      });
    };

    save = () => {
      this.setState({
        editing: false
      }, () => {
        if (this.props.onSave && this.isValueChanged()) {
          console.log('Value is changed', this.domElm.textContent);
        }
      });
    };

    cancel = () => {
      this.setState({
        editing: false
      });
    };

    isValueChanged = () => {
      return this.props.value !== this.domElm.textContent
    };

    handleKeyDown = (e) => {
      const { key } = e;
      switch (key) {
        case 'Enter':
        case 'Escape':
          this.save();
          break;
      }
    };

    render() {
      let editOnClick = true;
      const {editing} = this.state;
      if (this.props.editOnClick !== undefined) {
        editOnClick = this.props.editOnClick;
      }
      return (
        <WrappedComponent
          className={editing ? 'editing' : ''}
          onClick={editOnClick ? this.toggleEdit : undefined}
          contentEditable={editing}
          ref={(domNode) => {
            this.domElm = domNode;
          }}
          onBlur={this.save}
          onKeyDown={this.handleKeyDown}
          {...this.props}
      >
        {this.props.value}
      </WrappedComponent>
      )
    }
  }
}


class Todos extends Component {


  render(){
    let EditableDIV = contentEditable('div');
    let EditableP = contentEditable('p');
    
    const Todos = ({todos, deleteTodo, handleCheck}) => {
      const todoList = todos.length ? (
          todos.map(todo => {
              return (
              <div className="collection-item flex py-2" key={todo.id}>
                <div className="w-1/4">
                  <input type="checkbox" onChange={handleCheck}/>
                  <label className="ml-2">{todo.content}</label>
                </div>
                <div className="w-1/4">
                  <img src="/img/garbage.png" alt="delete" width="20px" onClick={() => {deleteTodo(todo.id)}}/>
                </div>
                <div className="w-1/4"><EditableP value={todo.quantity}/></div>
                <div className="w-1/4"><p>{todo.department}</p></div>
              </div>
              )
          })
      ) : (
          <p className="center">Add items to your shopping list</p>
      )
      }
      return(
          <div className="flex justify-center">
              <form onSubmit={this.handleSubmit} className="w-full p-4">
                <div className="bg-white h-8 xxl:h-12 mt-3 flex mt-2">
                  <input type = "text" onChange={this.handleChangeContent} value={this.state.content} placeholder="Add new ingredient or recipe" className="py-1 xxl:py-3 px-4 w-1/2"/>
                  <input type = "text" onChange={this.handleChangeQuantity} value={this.state.quantity} placeholder="Quantity" className="ml-2 py-1 xxl:py-3 px-4 w-1/5"/>
                  <select onChange={this.handleChangeDepartment} value={this.state.department} className="ml-2 py-1 xxl:py-3 px-4 w-1/4">
                    <option value="">Department</option>
                    <option value="Fruit and Veg">Fruit and Veg</option>
                    <option value="Dairy">Dairy</option>
                    <option value="Canned">Canned goods</option>
                  </select>
                  <button className="ml-2 bg-teal h-8 xxl:h-12 w-1/12 relative text-white">
                    <p>Add</p>
                  </button>
                </div>
              </form>
          </div>
      )
  }
}


export default Todos
