import React from 'react'

const Todos = ({todos, deleteTodo, handleCheck}) => {

    const todoList = todos.length ? (
        todos.map(todo => {
            return (
              <tr>
              <div className="collection-item flex justify-between py-2" key={todo.id}>
                <td>
                  <div>
                  <input type="checkbox" onClick={handleCheck}/>
                  <label className="ml-2">{todo.content}</label>
                </div>
                </td>
                <img src="/img/garbage.png" alt="delete" width="20px" onClick={() => {deleteTodo(todo.id)}}/>
                <div>{todo.quantity}</div>
                <div>{todo.department}</div>
              </div>
            </tr>
            )
        })
    ) : (
        <p className="center">Add items to your shopping list</p>
    )
    return (
        <table className="border">
          <tr>
            <th>Item</th>
            <th>Quantity</th>
            <th>Department</th>
          </tr>
            <div className="todos collection p-4">
            {todoList}
            </div>
        </table>
    )
}

export default Todos
