import React, { Component } from 'react'

class AddTodo extends Component {
    state = {
        content: '',
        quantity: '',
        department: ''
    }
    handleChange = (e) => {
        this.setState({
            content: e.target.value
        })
    }
    handleSubmit = (e) => {
       e.preventDefault();
       this.props.addTodo(this.state);
       this.setState({
           content:''
       })
    }
    render(){
        return(
            <div className="flex justify-center">
                <form onSubmit={this.handleSubmit} className="w-full p-4">
                  <div className="bg-white h-8 xxl:h-12 mt-3 flex mt-2">
                    <input type = "text" onChange={this.handleChange} value={this.state.content} placeholder="Add new ingredient or recipe" className="py-1 xxl:py-3 px-4 w-full"/>
                    <button className="bg-teal h-8 xxl:h-12 w-1/6 relative text-white">
                      <p>Add</p>
                    </button>
                  </div>
                </form>
            </div>
        )
    }
}

export default AddTodo
