import React, { Component } from 'react'

class AddTodo extends Component {
    state = {
        content: ''
    }
    handleChange = (e) => {
        this.setState({
            content: e.target.value
        })
    }
    handleSubmit = (e) => {
       e.preventDefault();
       this.props.addTodo(this.state);
       this.setState({
           content:''
       })
    }
    render(){
        return(
            <div>
                <form onSubmit={this.handleSubmit} className="bg-white h-8 xxl:h-12 mt-3 flex mt-2">
                    <input type = "text" onChange={this.handleChange} value={this.state.content} placeholder="Add new ingredient or recipe" className="text-gray-400 py-1 xxl:py-3 px-4"/>
                    <div className="bg-teal h-8 xxl:h-12 w-1/6 relative">
                    <div className="absolute transform-translate-center">
                      <img src="/img/search-white.png" className="ml-2 w-1/2" alt="search"/>
                    </div>
                  </div>
                </form>
            </div>
        )
    }
}

export default AddTodo
